<script id="card-template" type="text/x-handlebars-template">
    {{#each this}}  //循环
    <div class="card">
        <div>{{chinese @index}}</div>
        <div>姓名:{{name}}</div>
        {{#if birth}}  //判断
        <div>出生日期: {{birth}}</div>
        {{/if}}
        <div>出事地点: {{home}}</div>
        <div>职业: {{job}}</div>
        <ul>
            {{#each books}}
            <li {{#isfirst @index}} style="color: red;" {{/isfirst}}
                {{#isblus ../books}} style="color: blue;" {{/isblus}}>
