<!-- data  数据 -->
<script type="text/javascript">
    var data = [{
        name: '凯文米特尼克',
        birth: '1963年8月6日',
        home: '美国洛杉矶',
        job: '网络安全咨询师',
        books: [
            '《欺骗的艺术》',
            '《入侵的艺术》',
            '《欺骗的艺术》'
        ]
    }, {
        name: '沃兹尼亚克',
        birth: '1950年8月11日',
        home: '美国加利福尼亚',
        job: '电脑工程师',
        books: [
            '《与苹果一起疯狂》',
            '《自哈佛是空间》'
        ]

    }, {
        name: '大熊阿斯顿',
        home: '上海',
        job: '前端工程师',
    }];

    /*registerHelper 自定义标签*/

    Handlebars.registerHelper("chinese", function(value){
        var arr = ['第一', '第二', '第三'];
        return arr[value];
    });
    Handlebars.registerHelper("addone", function(value){
        return value + 1;
    });
    Handlebars.registerHelper("isfirst", function(value, options){
        if(value == 0){
            return options.fn(this);
        }
    });
    Handlebars.registerHelper("isblus", function(value, options){
        if(value && value.length >=2){
            return options.fn(this);
        }
    });


    var t = $('#card-template').html();  //拿到模版
    var f = Handlebars.compile(t);  //通过 handlebars 模版编译函数
    var h = f(data);  
    $('#card').html(h);
</script>
