(function($) {

    /*接口引用*/
    var GETCLASSES = "http://imoocnote.calfnote.com/inter/getClasses.php";

    /*ajax 全局函数*/
    $.ajaxSetup({
        error: function() {
            alert('调用接口失败');
            return false;
        }
    });

    /* 合二为一 定义一个函数 */
    function renderTemplate(templateSelector, data, htmlSelector) {
        var t = $(templateSelector).html();
        var f = Handlebars.compile(t);
        var h = f(data);
        $(htmlSelector).html(h);
    }

    function refreshClasses(curPage) {
        $.getJSON(GETCLASSES, {
            curPage: curPage
        }, function(data) {
            renderTemplate("#class-template", data.data, "#classes");
            renderTemplate("#pag-template", formatPag(data), "#pag");
            /*$('li.clickable').on('click', function(){
                $this = $(this);
                $this.data('id');*/
        });
    }

    function bindPageEvent() {
        $('#pag').delegate('li.clickable', 'click', function() { //delegate()委托事件
            $this = $(this);
            $this.data('id');
            refreshClasses($this.data('id'));
        });
    }
    bindPageEvent();

    $.getJSON(GETCLASSES, {
        curPage: 1
    }, function(data) {
        renderTemplate("#class-template", data.data, "#classes");
        renderTemplate("#pag-template", formatPag(data), "#pag");
        /*$('li.clickable').on('click', function(){
            $this = $(this);
            $this.data('id');
            refreshClasses($this.data('id'));
        });*/
    });


    /*ajax--渲染模版*/
    /*$.getJSON(GETCLASSES, {curPage: 1}, function(data){
        console.log(data);
        var t = $("#class-template").html();
        var f = Handlebars.compile(t);
        var h = f(data.data);
        $("#classes").html(h);

        var t = $("#pag-template").html();
        var f = Handlebars.compile(t);
        var h = f(formatPag(data));
        $("#pag").html(h);
    });*/


    /*是否有做笔记 的判断*/
    Handlebars.registerHelper("equal", function(v1, v2, options) {
        if (v1 == v2) {
            return options.fn(this);
        } else {
            return options.inverse(this);
        }
    });
    /*是否超过1个小时的判断*/
    Handlebars.registerHelper("long", function(v, options) {
        if (v.indexOf('小时') != -1) {
            return options.fn(this);
        } else {
            return options.inverse(this);
        }
    });
    /*分页*/
    function formatPag(pagData) {
        var arr = [];
        var total = parseInt(pagData.totalCount);
        var cur = parseInt(pagData.curPage);
        // 处理到首页的逻辑
        var toLeft = {};
        toLeft.index = 1;
        toLeft.text = '&laquo;';
        if (cur != 1) {
            toLeft.clickable = true;
        }
        arr.push(toLeft);

        //处理到上一页的逻辑
        var pre = {};
        pre.index = cur - 1;
        pre.text = '&lsaquo;';
        if (cur != 1) {
            pre.clickable = true;
        }
        arr.push(pre);

        //处理到cur页前的逻辑
        if (cur <= 5) {
            for (var i = 1; i < cur; i++) {
                var pag = {};
                pag.text = i;
                pag.index = i;
                pag.clickable = true;
                arr.push(pag);
            }
        } else {
            //如果cur>5,那么cur前的页面显示……
            var pag = {};
            pag.text = 1;
            pag.index = 1;
            pag.clickable = true;
            arr.push(pag);
            var pag = {};
            pag.text = '……';
            arr.push(pag);
            for (var i = cur - 2; i < cur; i++) {
                var pag = {};
                pag.text = i;
                pag.index = i;
                pag.clickable = true;
                arr.push(pag);
            }
        }
        //处理到cur页的逻辑
        var pag = {};
        pag.text = cur;
        pag.index = cur;
        pag.cur = true;
        arr.push(pag);

        //处理到cur页后的逻辑
        if (cur >= total - 4) {
            for (var i = cur + 1; i <= cur; i++) {
                var pag = {};
                pag.text = i;
                pag.index = i;
                pag.clickable = true;
                arr.push(pag);
            }
        } else {
            //如果cur <total-4,那么cur后的页面显示……
            for (var i = cur + 1; i <= cur + 2; i++) {
                var pag = {};
                pag.text = i;
                pag.index = i;
                pag.clickable = true;
                arr.push(pag);
            }
            var pag = {};
            pag.text = "……";
            arr.push(pag);
            var pag = {};
            pag.text = total;
            pag.index = total;
            pag.clickable = true;
            arr.push(pag);
        }
        //处理到一下页面的逻辑
        var next = {};
        next.index = cur + 1;
        next.text = '&rsaquo;';
        if (cur != total) {
            next.clickable = true;
        }
        arr.push(next);
        // 处理到尾页的逻辑
        var toRight = {};
        toRight.index = total;
        toRight.text = '&raquo;';
        if (cur != total) {
            toRight.clickable = true;
        }
        arr.push(toRight);
        return arr;
    };
})(jQuery);
